package com.santa.puzlle.y2022.daySix;

import static org.assertj.core.api.BDDAssertions.then;

import org.junit.jupiter.api.Test;

class PuzzleDaySixTest {
  @Test
  void signal4Digit_find4Different_return4() {
    final Marker marker = Signal.of("mjqpsdrt").findMarker();

    then(marker).isNotNull();
    then(marker.position()).isEqualTo(4);
  }

  @Test
  void signalTest1Digit_find4Different_return5() {
    final Marker marker =
        Signal.of(
                "")
            .findMarker();

    then(marker).isNotNull();
    then(marker.position()).isEqualTo(5);
  }
}
